mapper.update:
 		UPDATE 
		    ITEM
		SET 
		    ITEM_QTY = ITEM_QTY + #{item_qty}
		WHERE 
		    ITEM_PRODUCT_CODE = #{item_product_code}

UPDATE
	ITEM
SET
	ITEM_QTY = ITEM_QTY + #{di_qty}
WHERE 
	ITEM_PRODUCT_CODE = #{di_product_code}

update.foreach:
 		UPDATE 
		    ITEM
		SET 
		    ITEM_QTY = ITEM_QTY + #{item.di_qty}
		WHERE 
		    ITEM_PRODUCT_CODE = #{item.di_product_code}

mapper.update:
 		UPDATE 
		    ITEM
		SET 
		    ITEM_QTY = ITEM_QTY - #{item_qty}
		WHERE 
		    ITEM_PRODUCT_CODE = #{item_product_code}


UPDATE 
	OUT_ITEM
SET
	 OI_QTY = #{di_qty}
	,OI_TYPE = #{di_state}
WHERE
	OI_NO = #{di_no}

mapper.insert:

	SELECT NVL(MAX(OI_NO),0) +1 FROM OUT_ITEM

INSERT INTO DEFECT_ITEM
	(
	 DI_NO
	,DI_PRODUCT_CODE
	,DI_PRODUCT_NAME
	,DI_QTY
	,DI_STATE
	,DI_REASON
	) 
VALUES 
	(
	 #{di_no}
	,#{di_product_code}
	,#{di_product_name}
	,#{di_qty}
	,#{di_state}
	,#{di_reason}
	)

update.foreach:
 		UPDATE 
		    DEFECT_ITEM
		SET
		     DI_QTY = #{item.di_qty}
		    ,DI_STATE = #{item.di_state}
		    ,DI_REASON = #{item.di_reason}
		    ,DI_DATE = SYSDATE
		WHERE 
		    DI_NO = #{item.di_no}

insert.foreach:
INTO SAFE_ITEM
	(
	 SI_NO
	,SI_PRODUCT_CODE
	,SI_QTY
	) 
VALUES 
	(
	 ((SELECT NVL(MAX(SI_NO),0) +1 FROM SAFE_ITEM) + #{index})
	,#{item.si_product_code}
	,#{item.si_qty}
	)

update.foreach:
 		UPDATE 
		    SAFE_ITEM
		SET
		     SI_PRODUCT_CODE = #{item.si_product_code}
		    ,SI_QTY = #{item.si_qty}
		WHERE 
		    SI_NO = #{item.si_no}
