mapper.insert:

	SELECT NVL(MAX(DI_NO),0) +1 FROM DEFECT_ITEM

INSERT INTO DEFECT_ITEM
	(
	 DI_NO
	,DI_PRODUCT_CODE
	,DI_PRODUCT_NAME
	,DI_QTY
	,DI_STATE
	,DI_REASON
	) 
VALUES 
	(
	 #{di_no}
	,#{di_product_code}
	,#{di_product_name}
	,#{di_qty}
	,#{di_state}
	,#{di_reason}
	)

update.foreach:
 		UPDATE 
		    DEFECT_ITEM
		SET
		     DI_QTY = #{item.di_qty}
		    ,DI_STATE = #{item.di_state}
		    ,DI_REASON = #{item.di_reason}
		    ,DI_DATE = SYSDATE
		WHERE 
		    DI_NO = #{item.di_no}

