mapper.update:

		UPDATE ORDER_PLAN
		SET

      	ORDPL_QT = #{ORDPL_QT}

      WHERE 
      	ORDPL_ORDLIN_NUM = #{ORDPL_ORDLIN_NUM}
      AND
      	ORDPL_ONUM = #{ORDPL_ONUM}


UPDATE ORDER_INPUT
SET
	ORDIN_END = #{ORDIN_END}
WHERE
	ORDIN_NUM = #{ORDIN_NUM}


UPDATE BOOKMARK
SET
	BK_CODE = #{BK_CODE},
	BK_NAME = #{BK_CODE}

WHERE
	BK_CODE = #{BK_CODE}
