mapper.select:
select empregister_name as empregister_name 
from EmpRegister
where empregister_empnum = #{empregister_empnum} and empregister_jumin = #{empregister_jumin}

mapper.update:
UPDATE Schedule SET SCHEDULE_WORKINGEND = SYSDATE, schedule_workingtime = ROUND((sysdate - schedule_workingstart)*24*60*60) 
WHERE SCHEDULE_EMPREGISTER_EMPNUM = #{empregister_empnum}
AND to_char( Schedule_WorkingStart, 'yyyymmdd' ) = to_char( sysdate, 'yyyymmdd')


UPDATE salary 
SET 
	salary_empRegister_empnum = #{salary_empRegister_empnum},
	salary_hollydaystart= #{salary_hollydaystart},
	salary_hollydayend = #{salary_hollydayend}
	where salary_empRegister_empnum = #{salary_empRegister_empnum}
