mapper.select:
SELECT MT_CODE, MT_NAME FROM MENU_TYPE

      MT_CODE = #{mt_Code}

ORDER BY MT_CODE

SELECT MENU_CODE, MENU_MT_CODE, MENU_NAME, MENU_PRICE, MENU_IMAGE, MENU_STATE FROM MENU

      MENU_MT_CODE = #{menu_Mt_Code}
      AND MENU_CODE = #{menu_Code}
      AND MENU_STATE = #{menu_State}

 ORDER BY MENU_CODE

mapper.delete:
DELETE FROM ORDERS_DETAILS

      OD_ORDERS_NO = #{od_Orders_No}
      AND OD_MENU_CODE = #{od_Menu_Code}

mapper.select:
SELECT PAYMENT_NO, PAYMENT_SALES_NO, PAYMENT_MEMBER_ID, PAYMENT_TABLE, PAYMENT_STATE, PAYMENT_DATE, PAYMENT_POINT, PAYMENT_DISCOUNT, PAYMENT_CASH, PAYMENT_CARD FROM PAYMENT



      PAYMENT_TABLE = #{data} 


      PAYMENT_MEMBER_ID = #{data} 


      PAYMENT_STATE = #{data} 





            payment_Date >= #{predatepicker} and payment_Date < #{postdatepicker}



            payment_Date >= #{predatepicker}



            payment_Date < #{postdatepicker}






ORDER BY PAYMENT_DATE DESC

mapper.update:
{ CALL
BEGIN
    FOR I IN (SELECT INGREDIENT_QTY, INGREDIENT_PRODUCT_CODE FROM INGREDIENT WHERE INGREDIENT_MENU_CODE = #{od_Menu_Code}) LOOP
        UPDATE ITEM 
        SET ITEM_QTY = ITEM_QTY - (I.INGREDIENT_QTY) 
        WHERE ITEM_PRODUCT_CODE = (I.INGREDIENT_PRODUCT_CODE);
    END LOOP;
END }

mapper.insert:
{ CALL
BEGIN
    FOR I IN (SELECT INGREDIENT_QTY, INGREDIENT_PRODUCT_CODE FROM INGREDIENT WHERE INGREDIENT_MENU_CODE = #{od_Menu_Code}) LOOP
        INSERT INTO OUT_ITEM(OI_NO, OI_OD_NO, OI_PRODUCT_CODE, OI_PRODUCT_NAME, OI_QTY) 
        VALUES((SELECT COUNT(*) + 1 FROM OUT_ITEM), #{orders_No}, 
        I.INGREDIENT_PRODUCT_CODE,(SELECT PRODUCT_NAME FROM PRODUCT WHERE PRODUCT_CODE=I.INGREDIENT_PRODUCT_CODE), I.INGREDIENT_QTY);
    END LOOP;
END }

mapper.update:
{ CALL
BEGIN
    FOR I IN (SELECT INGREDIENT_QTY, INGREDIENT_PRODUCT_CODE FROM INGREDIENT WHERE INGREDIENT_MENU_CODE = #{od_Menu_Code}) LOOP
        UPDATE ITEM 
        SET ITEM_QTY = ITEM_QTY + (I.INGREDIENT_QTY) 
        WHERE ITEM_PRODUCT_CODE = (I.INGREDIENT_PRODUCT_CODE);
    END LOOP;
END }

{ CALL
BEGIN
    FOR I IN (SELECT INGREDIENT_QTY*OD_QTY AS QTY, INGREDIENT_PRODUCT_CODE FROM ORDERS_DETAILS, INGREDIENT 
    WHERE INGREDIENT.INGREDIENT_MENU_CODE = ORDERS_DETAILS.OD_MENU_CODE AND ORDERS_DETAILS.OD_ORDERS_NO = #{od_Orders_No}) LOOP
        UPDATE ITEM 
        SET ITEM_QTY = ITEM_QTY + (I.QTY) 
        WHERE ITEM_PRODUCT_CODE = (I.INGREDIENT_PRODUCT_CODE);
    END LOOP;
END }


{ CALL
DECLARE
 IGD_QTY NUMBER;
 ITEM_QTY NUMBER;
 MN_CODE VARCHAR2(20);
 BEGIN
   FOR I IN (SELECT INGREDIENT_QTY, ITEM_QTY, INGREDIENT_MENU_CODE FROM INGREDIENT 
   LEFT OUTER JOIN ITEM ON INGREDIENT.INGREDIENT_PRODUCT_CODE = ITEM.ITEM_PRODUCT_CODE WHERE INGREDIENT_MENU_CODE IN 
   (SELECT INGREDIENT_MENU_CODE FROM INGREDIENT LEFT OUTER JOIN ITEM ON INGREDIENT.INGREDIENT_PRODUCT_CODE = ITEM.ITEM_PRODUCT_CODE WHERE ITEM_QTY < INGREDIENT_QTY)) LOOP
   IGD_QTY := I.INGREDIENT_QTY;
   ITEM_QTY := I.ITEM_QTY;
   MN_CODE := I.INGREDIENT_MENU_CODE;
   IF IGD_QTY > ITEM_QTY THEN
      UPDATE MENU SET MENU_STATE = '품절' WHERE MENU_CODE = MN_CODE;
   END IF;
   END LOOP;
 END }
